  width: 100%;
  background-color: white;
  position: fixed;
  z-index: 2;

  .navbar {
    background-color: white;
    color: black;
    display: flex;
    justify-content: space-between;
    height: 90px;
    width: 1200px;
    margin: 0 auto;

    .logo {
      padding: 15px;
